import React, { useState } from "react";
import { RefreshCw } from "lucide-react"; // Icon library
import styles from "./LoginPage.module.css";
import { Swiper, SwiperSlide } from "swiper/react";
import { EffectFade, Autoplay } from "swiper/modules";

const LoginPage = () => {
  const [passwordVisible, setPasswordVisible] = useState(false);
  const [captcha, setCaptcha] = useState(generateCaptcha());

  function generateCaptcha() {
    return Math.random().toString(36).substring(2, 8).toUpperCase();
  }

  const refreshCaptcha = () => {
    setCaptcha(generateCaptcha());
  };

  return (
    <div className={styles.container}>
       
      {/* Login Section */}
      <div className={styles.loginSection}>
        <h2 className={styles.title}>Login</h2>
        <form className="col-6"> 
          <label className={styles.label}>Username</label>
          <input className={styles.input} type="text" placeholder="Enter your username" />
          
          <label className={styles.label}>Unit</label>
          <select className={styles.input}>
            <option value="">Select Unit</option>
            <option value="unit1">Unit 1</option>
            <option value="unit2">Unit 2</option>
          </select>

          <label className={styles.label}>Password</label>
          <div className={styles.passwordContainer}>
            <input 
              className={styles.input} 
              type={passwordVisible ? "text" : "password"} 
              placeholder="Enter your password" 
            />
            <span 
              className={styles.hideIcon} 
              onClick={() => setPasswordVisible(!passwordVisible)}
            >
              {passwordVisible ? "Hide" : "Show"}
            </span>
          </div>

          <label className={styles.label}>Enter Captcha</label>
          <div className={styles.captchaContainer}>
            <div className={styles.captcha}>{captcha}</div>
            <button 
              type="button" 
              className={styles.refreshButton} 
              onClick={refreshCaptcha}
            >
              <RefreshCw size={18} />
            </button>
          </div>
          <br></br>
          <input 
            className={styles.input} 
            type="text" 
            placeholder="Enter Captcha text" 
          />

          <div className={styles.checkboxContainer}>
            <input type="checkbox" id="terms" />
            <label htmlFor="terms">
              By creating an account, I agree to the{" "}
              <a href="/" className={styles.link}>Terms of use</a> and{" "}
              <a href="/" className={styles.link}>Privacy Policy</a>.
            </label>
          </div>

          <button type="submit" className={styles.submitButton}>Sign in</button>
        </form>
      </div>

      <div className={styles.imageSection}>
      {/* Swiper Background Slider */}
      <Swiper
        effect="fade"
        modules={[EffectFade, Autoplay]}
        autoplay={{ delay: 3000 }}
        loop={true}
        className={styles.backgroundSlider} // Style the Swiper container
      >
        <SwiperSlide>
          <img src="/images/slider1.jpg" alt="Slide 1" className={styles.slideImage} />
        </SwiperSlide>
        <SwiperSlide>
          <img src="/images/slider2.jpg" alt="Slide 2" className={styles.slideImage} />
        </SwiperSlide>
        <SwiperSlide>
          <img src="/images/slider3.jpg" alt="Slide 3" className={styles.slideImage} />
        </SwiperSlide>
        <SwiperSlide>
          <img src="/images/slider4.jpg" alt="Slide 4" className={styles.slideImage} />
        </SwiperSlide>
      </Swiper>

      {/* Overlay Text */}
      <h1 className={styles.welcomeTitle}>Welcome</h1>
      <h3 className={styles.welcomeText}>
        Enter your personal details and start your journey with us.
      </h3>
    </div>
      {/* <div className={styles.imageSection} > 
        <h1 className={styles.welcomeTitle}>Welcome</h1>
        <h3 className={styles.welcomeText}>
          Enter your personal details and start your journey with us.
        </h3>
      </div> */}




      
{/* 
      Image Gallery */}
     {/* <div className={styles.imageGallery}>
       <img src="/assets/img1.jpg" alt="Image 1" className={styles.animatedImage} />
       <img src="/assets/img2.jpg" alt="Image 2" className={styles.animatedImage} />
       <img src="/assets/img3.jpg" alt="Image 3" className={styles.animatedImage} />
       <img src="/assets/img4.jpg" alt="Image 4" className={styles.animatedImage} />
    </div> */}

    </div>
  );
};

export default LoginPage;
