import React from 'react';

const LoginPage = () => {
  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: '100vh',
      backgroundColor: '#34B3F1',
      padding: '1.5rem', // Added padding for mobile view

    },
    card: {
      display: 'flex',
      flexDirection: 'row', // Default direction for desktop
      width: '100%',
      maxWidth: '1200px',
      height: '500px',
      borderRadius: '8px',
      overflow: 'hidden',
      boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
      backgroundColor: '#fff',
    },
    imageContainer: {
      flex: '0 0 40%', // 40% width for desktop
      backgroundImage: 'url(https://plus.unsplash.com/premium_photo-1661434779070-cf8fc0e253ab?fm=jpg&q=60&w=3000&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MXx8bGFifGVufDB8fDB8fHww)',
      backgroundSize: 'cover',
      backgroundPosition: 'center',
    },
    formContainer: {
      flex: '0 0 40%', // 60% width for desktop
      padding: '1.5rem',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
    },
    title: {
      fontSize: '24px',
      fontWeight: 'bold',
      marginBottom: '1.5rem',
      textAlign: 'center',
    },
    formGroup: {
      marginBottom: '1rem',
    },
    label: {
      display: 'block',
      marginBottom: '0.5rem',
      fontSize: '14px',
      color: '#333',
    },
    input: {
      width: '100%',
      padding: '0.8rem',
      border: '1px solid #ccc',
      borderRadius: '4px',
      fontSize: '14px',
    },
    button: {
      width: '100%',
      padding: '0.8rem',
      backgroundColor: '#34B3F1',
      color: '#fff',
      border: 'none',
      borderRadius: '4px',
      fontSize: '16px',
      cursor: 'pointer',
      marginTop: '1rem',
    },
    link: {
      marginTop: '1rem',
      textAlign: 'center',
      fontSize: '14px',
    },
    mobile: {
      '@media (max-width: 768px)': {
        card: {
          flexDirection: 'column', // Stack the image and form vertically
          height: 'auto', // Adjust height for mobile
        },
        imageContainer: {
          flex: '0 0 auto',
          height: '200px', // Fixed height for the image on mobile
        },
        formContainer: {
          flex: '0 0 auto',
          padding: '1.5rem',
        },
      },
    },
  };

  return (
    <div style={styles.container}>
      <div style={{ ...styles.card, ...(window.innerWidth <= 768 && styles.mobile.card) }}>
        {/* Left Side Image */}
        <div
          style={{
            ...styles.imageContainer,
            ...(window.innerWidth <= 768 && styles.mobile.imageContainer),
          }}
        ></div>

        {/* Right Side Form */}
        <div
          style={{
            ...styles.formContainer,
            ...(window.innerWidth <= 768 && styles.mobile.formContainer),
          }}
        >
          <h2 style={styles.title}>Login</h2>
          <form>
            <div style={styles.formGroup}>
              <label style={styles.label}>Username</label>
              <input type="text" placeholder="Enter your username" style={styles.input} />
            </div>
            <div style={styles.formGroup}>
              <label style={styles.label}>Password</label>
              <input type="password" placeholder="Enter your password" style={styles.input} />
            </div>
            <button type="submit" style={styles.button}>
              Login
            </button>
            <div style={styles.link}>
              <a href="#">Forgot Password?</a>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
