import React from "react";
import { useState } from "react";

const Login = () => {
  const [captcha, setCaptcha] = useState("SSUd86");
  const [currentSlide, setCurrentSlide] = useState(0);

  const handleNext = () => {
    setCurrentSlide((prev) => (prev + 1) % images.length);
  };

  const handlePrev = () => {
    setCurrentSlide((prev) => (prev - 1 + images.length) % images.length);
  };



  const images = [
    "https://media.istockphoto.com/id/1346675635/photo/modern-medical-research-laboratory-portrait-of-latin-and-black-young-scientists-using.jpg?s=612x612&w=0&k=20&c=9PiRizOrO04C6TSyMhqv7NUDabjhbrrnL3LVLuArGJU=/300x200?text=Image+1",
    "https://media.istockphoto.com/id/1346675527/photo/medical-science-laboratory-portrait-of-beautiful-black-scientist-looking-under-microscope.jpg?s=612x612&w=0&k=20&c=xDRiQ1uX5bg0k3mU9SnsOIM6DSeeex-N3uFVSDgRt1w=/300x200?text=Image+2",
    "https://media.istockphoto.com/id/1354172647/photo/medical-research-laboratory-portrait-of-a-beautiful-female-scientist-in-goggles-using-micro.jpg?s=612x612&w=0&k=20&c=hfA56VJIRSa2MEE9r4-o8sVb6SH-18DeTjyoh5gsdyI=/300x200?text=Image+3",
    "https://media.istockphoto.com/id/1354172647/photo/medical-research-laboratory-portrait-of-a-beautiful-female-scientist-in-goggles-using-micro.jpg?s=612x612&w=0&k=20&c=hfA56VJIRSa2MEE9r4-o8sVb6SH-18DeTjyoh5gsdyI=/300x200?text=Image+4",
    "https://media.istockphoto.com/id/1354172647/photo/medical-research-laboratory-portrait-of-a-beautiful-female-scientist-in-goggles-using-micro.jpg?s=612x612&w=0&k=20&c=hfA56VJIRSa2MEE9r4-o8sVb6SH-18DeTjyoh5gsdyI=/300x200?text=Image+5",
  ];

  const refreshCaptcha = () => {
    // Generate a random captcha string (placeholder implementation)
    const randomCaptcha = Math.random().toString(36).substring(2, 8);
    setCaptcha(randomCaptcha);
  };

  const styles = {
    loginContainer: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      height: "100vh",
      backgroundColor: "#f0f4ff",
    },
    loginCard: {
      display: "flex",
      width: "70%",
      maxWidth: "1000px",
      boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
      borderRadius: "8px",
      overflow: "hidden",
    },
    loginForm: {
      flex: "0 0 40%",
      padding: "2rem",
      backgroundColor: "#ffffff",
  
    },
    loginTitle: {
      fontSize: "24px",
      fontWeight: "bold",
      marginBottom: "1.5rem",
    },
    formGroup: {
      marginBottom: "1rem",
    },
    label: {
      display: "block",
      fontSize: "14px",
      marginBottom: "0.5rem",
    },
    input: {
      width: "100%",
      padding: "0.8rem",
      border: "1px solid #ccc",
      borderRadius: "4px",
    },
    select: {
      width: "100%",
      padding: "0.8rem",
      border: "1px solid #ccc",
      borderRadius: "4px",
    },
    captchaContainer: {
      display: "flex",
      alignItems: "center",
      gap: "0.5rem",
    },
    captchaBox: {
      padding: "0.5rem 1rem",
      backgroundColor: "#f3f4f6",
      border: "1px solid #ddd",
      borderRadius: "4px",
      fontFamily: "monospace",
    },
    refreshButton: {
      background: "none",
      border: "none",
      color: "#007bff",
      cursor: "pointer",
    },
    terms: {
      display: "flex",
      alignItems: "center",
      gap: "0.5rem",
      fontSize: "14px",
    },
    termsLink: {
      color: "#007bff",
      textDecoration: "none",
    },
    termsLinkHover: {
      textDecoration: "underline",
    },
    submitButton: {
      width: "100%",
      padding: "0.8rem",
      backgroundColor: "#007bff",
      color: "white",
      border: "none",
      borderRadius: "4px",
      cursor: "pointer",
      fontSize: "16px",
    },
    submitButtonHover: {
      backgroundColor: "#0056b3",
    },
    welcomeSection: {
      flex: "0 0 60%",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      background: "url('https://thumbs.dreamstime.com/b/lab-assistant-studying-samples-to-detect-pathologies-quality-medical-research-stock-video-105848572.jpg') no-repeat center center",
      backgroundSize: "cover",
      color: "white",
      textAlign: "center",
      padding: "2rem",
    },
    welcomeContent: {
      textAlign: "center",
    },
    welcomeTitle: {
      fontSize: "36px",
      fontWeight: "bold",
    },
    welcomeText: {
      marginTop: "1rem",
      fontSize: "18px",
    },
  };

  return (
    <div style={styles.loginContainer}>
      <div style={styles.loginCard}>
        {/* Left Section - Login Form */}
        <div style={styles.loginForm}>
          <h2 style={styles.loginTitle}>Login</h2>

          <form>
            <div style={styles.formGroup}>
              <label style={styles.label}>Username</label>
              <input type="text" placeholder="Enter your username" style={styles.input} />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Unit</label>
              <select style={styles.select}>
                <option value="">Select Unit</option>
                <option value="unit1">Unit 1</option>
                <option value="unit2">Unit 2</option>
                <option value="unit3">Unit 3</option>
              </select>
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Password</label>
              <input type="password" placeholder="Enter your password" style={styles.input} />
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Enter Captcha</label>
              <div style={styles.captchaContainer}>
                <div style={styles.captchaBox}>{captcha}</div>
                <button
                  type="button"
                  style={styles.refreshButton}
                  onClick={refreshCaptcha}
                >
                  ↻
                </button>
              </div>
              <input type="text" placeholder="Enter Captcha text" style={styles.input} />
            </div>

            <div style={styles.formGroup}>
              <input type="checkbox" id="terms" />
              <label htmlFor="terms" style={styles.label}>
                By creating an account, I agree to our
                <a href="#" style={styles.termsLink}> Terms of use </a> and
                <a href="#" style={styles.termsLink}> Privacy Policy</a>
              </label>
            </div>

            <button style={styles.submitButton}>Sign in</button>
          </form>
        </div>


         {/* Right Section - Image Slider */}
         <div style={styles.sliderContainer}>
          <img
            src={images[currentSlide]}
            alt={`Slide ${currentSlide + 1}`}
            style={styles.image}
          />
          <button
            style={{ ...styles.navigationButton, ...styles.prevButton }}
            onClick={handlePrev}
          >
            {"<"}
          </button>
          <button
            style={{ ...styles.navigationButton, ...styles.nextButton }}
            onClick={handleNext}
          >
            {">"}
          </button>
        </div>

        {/* Right Section - Welcome Message */}
        <div style={styles.welcomeSection}>
          <div style={styles.welcomeContent}>
            <h2 style={styles.welcomeTitle}>Welcome</h2>
            <p style={styles.welcomeText}>Enter your personal details and start your journey with us</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
